export default {
  props: {
    editedItem: Object,
    showEditModal: Boolean,
    statusOptions: {
      type: Array,
      default: () => [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }]
    }
  },
  data() {
    return {
      showModal: false
    };
  },
  watch: {
    showEditModal(newVal) {
      this.showModal = newVal;
    }
  },
  methods: {
    handleSubmit() {
      this.$emit('submit-edit', this.editedItem);
      this.closeModal();
    },
    closeModal() {
      this.$emit('close-modal');
    }
  }
};
