export default {
  props: {
    product: Object
  },
  data() {
    return {
      productData: {
        id: '',
        title: '',
        description: '',
        due_date: '',
        status: ''
      },
      statusOptions: [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }],
      isModalVisible: false
    };
  },
  mounted() {
    this.initializeProductData();
  },
  watch: {
    product: {
      handler(newVal) {
        this.initializeProductData();
      },
      immediate: true
    }
  },
  methods: {
    initializeProductData() {
      this.productData = {
        ...this.product
      };
    },
    showModal() {
      this.isModalVisible = true;
    },
    hideModal() {
      this.isModalVisible = false;
    },
    handleSubmit() {
      this.$emit('update-product', this.productData);
      this.hideModal();
    }
  }
};
