export default {
  props: {
    editedItem: Object,
    showEditModal: Array,
    statusOptions: {
      type: Array,
      default: () => [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }]
    },
    index: Number
  },
  data() {
    return {
      showModal: false,
      editedItemCopy: {}
    };
  },
  watch: {
    showEditModal(newVal) {
      this.showModal = newVal[this.index];
      if (newVal[this.index]) {
        this.editedItemCopy = {
          ...this.editedItem
        };
      }
    }
  },
  methods: {
    submitEditForm() {
      this.$emit('submit-edit', this.editedItemCopy);
      this.closeModal();
    },
    closeModal() {
      this.$emit('close-modal', this.index);
    }
  }
};
