export default {
  props: {
    editedItem: Object,
    showEditModal: Boolean,
    statusOptions: {
      type: Array,
      default: () => [{
        value: 1,
        text: 'Active'
      }, {
        value: 0,
        text: 'Expired'
      }]
    }
  },
  data() {
    return {
      showModal: false,
      editedItemCopy: {}
    };
  },
  watch: {
    showEditModal(newVal) {
      this.showModal = newVal;
      if (newVal) {
        this.editedItemCopy = {
          ...this.editedItem
        };
      }
    }
  },
  methods: {
    submitEditForm() {
      this.$emit('submit-edit', this.editedItemCopy);
    },
    onHidden() {
      this.$emit('cancel-edit');
    }
  }
};
