export default {
  data() {
    return {
      newProduct: {
        title: '',
        description: '',
        due_date: '',
        status: ''
      },
      statusOptions: [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }],
      isModalVisible: false,
      showModal() {
        this.isModalVisible = true;
      },
      handleSubmit() {
        // Add new product to the products array
        const newId = this.products.length + 1;
        this.products.push({
          ...this.newProduct,
          id: newId
        });
        // Reset new product form
        this.newProduct = {
          title: '',
          description: '',
          due_date: '',
          status: ''
        };
        // Hide the modal
        this.isModalVisible = false;
      }
    };
  }
};
