import { mapActions } from 'vuex';
import Swal from 'sweetalert2';
export default {
  props: {
    editedItem: {
      type: Object,
      required: true
    },
    showEditModal: {
      type: Boolean,
      required: true
    },
    statusOptions: {
      type: Array,
      default: () => [{
        value: '1',
        text: 'Active'
      }, {
        value: '0',
        text: 'Expired'
      }]
    }
  },
  data() {
    return {
      showModal: false,
      editedItemCopy: {}
    };
  },
  watch: {
    showEditModal(newVal) {
      this.showModal = newVal;
      if (newVal) {
        this.editedItemCopy = {
          ...this.editedItem
        };
        if (!this.editedItemCopy.status) {
          if (this.editedItem.status === '1') {
            this.editedItemCopy.status = '1';
          } else if (this.editedItem.status === '0') {
            this.editedItemCopy.status = '0';
          } else {
            // Handle other cases if needed
          }
        }
      }
    },
    showModal(newVal) {
      if (!newVal) {
        this.$emit('update:showEditModal', false);
      }
    }
  },
  methods: {
    ...mapActions(['updateProduct']),
    async submitEditForm() {
      try {
        const updatedProduct = await this.updateProduct(this.editedItemCopy);
        this.$emit('submit-edit', updatedProduct);
        this.showModal = false;
        Swal.fire({
          icon: 'success',
          title: 'Success!',
          text: 'Product updated successfully',
          showConfirmButton: false,
          timer: 1500
        });
      } catch (error) {
        console.error('Error updating product:', error);
      }
    },
    onHidden() {
      this.$emit('cancel-edit');
    }
  }
};
