export default {
  props: {
    product: Object
  },
  data() {
    return {
      productData: {
        ...this.product
      },
      statusOptions: [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }],
      isModalVisible: false
    };
  },
  watch: {
    product(newVal) {
      this.productData = {
        ...newVal
      };
    }
  },
  methods: {
    showModal() {
      this.isModalVisible = true;
    },
    hideModal() {
      this.isModalVisible = false;
    },
    handleSubmit() {
      this.$emit('update-product', this.productData);
      this.hideModal();
    }
  }
};
