export default {
  data() {
    return {
      newProduct: {
        title: '',
        description: '',
        due_date: '',
        status: ''
      },
      statusOptions: [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }],
      isModalVisible: false
    };
  },
  methods: {
    showModal() {
      this.isModalVisible = true;
    },
    handleSubmit() {
      // Emit event to parent to add new product
      this.$emit('add-product', {
        ...this.newProduct,
        id: this.generateId()
      });
      // Reset new product form
      this.newProduct = {
        title: '',
        description: '',
        due_date: '',
        status: ''
      };
      // Hide the modal
      this.isModalVisible = false;
    },
    generateId() {
      return Date.now();
    }
  }
};
