export default {
  props: {
    editedItem: Object,
    showEditModal: Boolean,
    statusOptions: {
      type: Array,
      default: () => [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }]
    }
  },
  data() {
    return {
      showModal: false,
      editedItemCopy: null
    };
  },
  watch: {
    showEditModal(newVal) {
      this.showModal = newVal;
      if (newVal) {
        this.editedItemCopy = {
          ...this.editedItem
        };
      }
    }
  },
  methods: {
    handleSubmit() {
      this.$emit('submit-edit', this.editedItemCopy);
      this.closeModal();
    },
    closeModal() {
      this.$emit('close-modal');
      this.editedItemCopy = null;
    }
  }
};
