export default {
  props: {
    editedItem: Object,
    showModal: Boolean,
    statusOptions: {
      type: Array,
      default: () => [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }]
    }
  },
  data() {
    return {
      showModal: false,
      editedItemCopy: {}
    };
  },
  watch: {
    showModal(newVal) {
      this.showModal = newVal;
      if (newVal) {
        // Make a copy of the editedItem prop for local modifications
        this.editedItemCopy = {
          ...this.editedItem
        };
      }
    }
  },
  methods: {
    submitEditForm() {
      // Emit the edited item to the parent component
      this.$emit('submit-edit', this.editedItemCopy);
    },
    closeModal() {
      this.$emit('update:showEditModal', false); // Emit an event to update the prop value
    },
    handleEditModalUpdate(value) {
      this.showEditModal = value;
    }
  }
};
