import AddModal from "@/components/Modal/AddModal.vue";
import EditModal from "@/components/Modal/EditModal.vue";
import { mapState, mapActions } from 'vuex';
import Swal from 'sweetalert2';
export default {
  name: 'App',
  components: {
    AddModal,
    EditModal
  },
  data() {
    return {
      fields: [{
        key: 'id',
        label: 'ID'
      }, {
        key: 'title',
        label: 'Title'
      }, {
        key: 'description',
        label: 'Description'
      }, {
        key: 'due_date',
        label: 'Due Date'
      }, {
        key: 'status',
        label: 'Status'
      }, {
        key: 'actions',
        label: 'Actions'
      }],
      statusOptions: [{
        value: 1,
        text: 'Active'
      }, {
        value: 0,
        text: 'Expired'
      }],
      showEditModal: false,
      editedItem: {}
    };
  },
  computed: {
    ...mapState({
      products: state => state.products
    }),
    productsWithStatus() {
      return this.products.map(product => ({
        ...product,
        status: this.mapStatus(product.status)
      }));
    }
  },
  methods: {
    ...mapActions(['getProducts', 'updateProduct']),
    showModal() {
      this.$refs.addModal.showModal();
    },
    openEditModal(item) {
      this.editedItem = {
        ...item
      };
      this.showEditModal = true;
    },
    closeEditModal() {
      this.showEditModal = false;
    },
    async updateEditedItem(updatedItem) {
      try {
        const updatedProduct = await this.updateProduct(updatedItem);
        Swal.fire({
          title: 'Success!',
          text: 'Product successfully updated',
          icon: 'success',
          confirmButtonText: 'OK'
        });
        this.showEditModal = false;
      } catch (error) {
        console.error('Error updating product:', error);
      }
    },
    async deleteProduct(id) {
      try {
        await this.$store.dispatch('deleteProduct', id);
        Swal.fire({
          title: 'Success!',
          text: 'Product successfully deleted',
          icon: 'success',
          confirmButtonText: 'OK'
        });
      } catch (error) {
        console.error('Error deleting product:', error);
      }
    },
    addProduct(newProduct) {
      this.$store.commit('ADD_PRODUCT', newProduct);
    },
    mapStatus(status) {
      return status === 1 ? 'active' : 'expired';
    }
  },
  created() {
    this.getProducts();
  }
};
