import { useStore } from 'vuex';
import { ref } from 'vue';
import { disableButton } from '../Helper/functions.js';
export default {
  name: 'AddModal',
  setup() {
    const store = useStore();
    const isModalVisible = ref(false);
    const newProduct = ref({
      title: '',
      description: '',
      due_date: '',
      status: ''
    });
    const statusOptions = ref([{
      value: 'active',
      text: 'Active'
    }, {
      value: 'expired',
      text: 'Expired'
    }]);
    const handleSubmit = () => {
      disableButton('.btn-save', true);
      store.dispatch('product/createProduct', newProduct.value).then(res => {
        store.dispatch('product/fetchProducts'); // Refresh the product list (if needed)
        this.$message.success(res.data.message);
        newProduct.value = {
          title: '',
          description: '',
          due_date: '',
          status: ''
        }; // Reset the form
        isModalVisible.value = false; // Hide the modal
        disableButton('.btn-save', false);
      }).catch(err => {
        this.$message.error(err);
        disableButton('.btn-save', false);
      });
    };
    return {
      isModalVisible,
      newProduct,
      statusOptions,
      handleSubmit
    };
  }
};
