import { disableButton } from "../Helper/functions.js";
export default {
  data() {
    return {
      newProduct: {
        title: '',
        description: '',
        due_date: '',
        status: ''
      },
      statusOptions: [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }],
      isModalVisible: false
    };
  },
  methods: {
    showModal() {
      this.isModalVisible = true;
    },
    handleSubmit() {
      disableButton('.btn-save', true);
      this.$store.dispatch('product/createProduct', this.newProduct).then(res => {
        this.$message.success(res.data.message);
        // Reset new product form
        this.newProduct = {
          title: '',
          description: '',
          due_date: '',
          status: ''
        };
        // Hide the modal
        this.isModalVisible = false;
        disableButton('.btn-save', false);
      }).catch(err => {
        this.$message.error(err);
        disableButton('.btn-save', false);
      });
    },
    generateId() {
      return Date.now();
    }
  }
};
