import Swal from 'sweetalert2';
import { mapActions } from 'vuex';
import { disableButton } from "../Helper/functions.js";
export default {
  data() {
    return {
      newProduct: {
        title: '',
        description: '',
        due_date: '',
        status: ''
      },
      statusOptions: [{
        value: 'active',
        text: 'Active'
      }, {
        value: 'expired',
        text: 'Expired'
      }],
      isModalVisible: false
    };
  },
  methods: {
    ...mapActions(['createProduct']),
    showModal() {
      this.isModalVisible = true;
    },
    handleSubmit() {
      disableButton('.btn-save', true);
      this.createProduct(this.newProduct).then(res => {
        // Success message
        Swal.fire({
          icon: 'success',
          title: 'Success',
          text: res.data.message
        });
        this.resetForm();
      }).catch(err => {
        // Error message
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: err.message || 'An error occurred'
        });
        disableButton('.btn-save', false);
      });
    },
    resetForm() {
      this.newProduct = {
        title: '',
        description: '',
        due_date: '',
        status: ''
      };
      this.isModalVisible = false;
    }
  }
};
