import {computed, type MaybeRefOrGetter, toValue} from 'vue'
import type {AlignmentJustifyContent} from '../types'

export default (align: MaybeRefOrGetter<AlignmentJustifyContent | undefined>) =>
  computed(() => {
    const value = toValue(align)
    return !value ? '' : `justify-content-${value}`
  })
import type {AriaInvalid} from '../types'
import {computed, type MaybeRefOrGetter, toValue} from 'vue'

export default (
  ariaInvalid: MaybeRefOrGetter<AriaInvalid | undefined>,
  state: MaybeRefOrGetter<boolean | null | undefined>
) =>
  computed(() => {
    const resolvedAriaInvalid = toValue(ariaInvalid)
    const resolvedState = toValue(state)

    const resolvedAriaInvalidValue =
      resolvedAriaInvalid === true
        ? 'true'
        : typeof resolvedAriaInvalid === 'string'
          ? resolvedAriaInvalid
          : resolvedState === false
            ? 'true'
            : resolvedAriaInvalid === false
              ? 'false'
              : undefined

    return resolvedAriaInvalidValue
  })
/**
 * Items that are omitted from the BvEventInit constructor arg
 */
export type BvEventConstructorOmittables = 'eventType' | 'defaultPrevented'

export default class BvEvent {
  readonly cancelable: boolean = true
  readonly componentId: string | null = null
  private _defaultPrevented = false
  readonly eventType: string = ''
  readonly nativeEvent: string | null = null
  private _preventDefault: () => void
  readonly relatedTarget: EventTarget | null = null
  readonly target: EventTarget | null = null
  // Readable by everyone,
  // But only overwritten by inherrited constructors
  public get defaultPrevented() {
    return this._defaultPrevented
  }
  protected set defaultPrevented(prop) {
    this._defaultPrevented = prop
  }
  // I think this is right
  // We want to be able to have it callable to everyone,
  // But only overwritten by inherrited constructors
  public get preventDefault() {
    return this._preventDefault
  }
  // This may not be correct, because it doesn't get correct type inferences in children
  // Ex overwrite this.preventDefault = () => true is valid. Could be a TS issue
  protected set preventDefault(setter: () => void) {
    this._preventDefault = setter
  }

  constructor(
    eventType: string,
    eventInit: Partial<Omit<BvEvent, BvEventConstructorOmittables>> = {}
  ) {
    if (!eventType) {
      throw new TypeError(
        `Failed to construct '${this.constructor.name}'. 1 argument required, ${arguments.length} given.`
      )
    }

    // Merge defaults first, the eventInit, and the type last
    // so, it can't be overwritten
    Object.assign(this, BvEvent.Defaults, eventInit, {eventType})

    this._preventDefault = function _preventDefault() {
      if (this.cancelable) {
        this.defaultPrevented = true
      }
    }
  }

  static get Defaults() {
    return {
      cancelable: true,
      componentId: null,
      eventType: '',
      nativeEvent: null,
      relatedTarget: null,
      target: null,
    }
  }
}
import BvEvent, {type BvEventConstructorOmittables} from './BvEvent'

export default class BvTriggerableEvent extends BvEvent {
  readonly trigger: string | null = null

  constructor(
    eventType: string,
    eventInit: Partial<Omit<BvTriggerableEvent, BvEventConstructorOmittables>> = {}
  ) {
    super(eventType, eventInit)

    // Merge defaults first, the eventInit, and the type last
    // so, it can't be overwritten
    Object.assign(this, BvEvent.Defaults, eventInit, {eventType})
  }

  static get Defaults() {
    return {
      ...super.Defaults,
      trigger: null,
    }
  }
}
import BvEvent, {type BvEventConstructorOmittables} from './BvEvent'

export default class BvCarouselEvent extends BvEvent {
  readonly from: number
  readonly to: number
  readonly direction: 'left' | 'right'

  constructor(
    eventType: string,
    eventInit: Partial<Omit<BvEvent, BvEventConstructorOmittables>> & {
      from: number
      to: number
      direction: 'left' | 'right'
    }
  ) {
    super(eventType, eventInit)
    Object.assign(this, BvEvent.Defaults, eventInit, {eventType})
    const {from, direction, to} = eventInit
    this.from = from
    this.to = to
    this.direction = direction
  }

  static get Defaults() {
    return {
      ...super.Defaults,
    }
  }
}
import type {Slot} from 'vue'

/**
 * @deprecated only used in BFormGroup, which is not an SFC... Function could probably be replaced with pure Vue
 */
export const attemptFocus = (
  el: Readonly<HTMLElement>,
  options: Readonly<FocusOptions> = {}
): boolean => {
  const getActiveElement = (excludes: readonly HTMLElement[] = []): Element | null => {
    const {activeElement} = document
    return activeElement && !excludes.some((el) => el === activeElement) ? activeElement : null
  }

  const isActiveElement = (el: Readonly<HTMLElement>): boolean => el === getActiveElement()

  try {
    el.focus(options)
  } catch (e) {
    // eslint-disable-next-line no-console
    console.error(e)
  }
  return isActiveElement(el)
}

export const isEmptySlot = (el: Slot | undefined): boolean => (el?.() ?? []).length === 0

/**
 * @deprecated only used in BFormGroup, which is not an SFC... Function could probably be replaced with pure Vue
 */
export const isVisible = (el: Readonly<Element>): boolean => {
  //if (!isElement(el) || !el.parentNode || !contains(DOCUMENT.body, el)) {
  // Note this can fail for shadow dom elements since they
  // are not a direct descendant of document.body
  //return false
  //}
  if (el.getAttribute('display') === 'none') {
    // We do this check to help with vue-test-utils when using v-show
    return false
  }
  // All browsers support getBoundingClientRect(), except JSDOM as it returns all 0's for values :(
  // So any tests that need isVisible will fail in JSDOM
  // Except when we override the getBCR prototype in some tests
  const bcr = el.getBoundingClientRect()
  return !!(bcr && bcr.height > 0 && bcr.width > 0)
}

export const getTransitionDelay = (element: Readonly<HTMLElement>) => {
  const style = window.getComputedStyle(element)
  // if multiple durations are defined, we take the first
  const transitionDelay = style.transitionDelay.split(',')[0] || ''
  const transitionDuration = style.transitionDuration.split(',')[0] || ''
  const transitionDelayMs = Number(transitionDelay.slice(0, -1)) * 1000
  const transitionDurationMs = Number(transitionDuration.slice(0, -1)) * 1000
  return transitionDelayMs + transitionDurationMs
}
/**
 * @deprecated remove with parseEventOptions
 */
export const IS_BROWSER =
  typeof window !== 'undefined' &&
  typeof document !== 'undefined' &&
  typeof navigator !== 'undefined'

// Normalize event options based on support of passive option
// Exported only for testing purposes
/**
 * @deprecated
 */
export const parseEventOptions = (
  options: boolean | Readonly<EventListenerOptions> | undefined
): boolean | EventListenerOptions | undefined => {
  const HAS_PASSIVE_EVENT_SUPPORT = (() => {
    let passiveEventSupported = false
    if (IS_BROWSER) {
      try {
        const options = {
          // This function will be called when the browser
          // attempts to access the passive property
          get passive() {
            passiveEventSupported = true
            // eslint-disable-next-line no-useless-return
            return
          },
        }
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        WINDOW.addEventListener('test', options, options)
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        WINDOW.removeEventListener('test', options, options)
      } catch {
        passiveEventSupported = false
      }
    }
    return passiveEventSupported
  })()

  if (HAS_PASSIVE_EVENT_SUPPORT) {
    return typeof options === 'object' ? options : {capture: !!options || false}
  }
  // Need to translate to actual Boolean value
  return typeof options === 'object' ? options.capture : options
}

// Attach an event listener to an element
/**
 * @deprecated
 */
export const eventOn = (
  el: Readonly<Element>,
  eventName: string,
  handler: EventListener,
  options: boolean | Readonly<EventListenerOptions> | undefined
) => {
  if (el && el.addEventListener) {
    el.addEventListener(eventName, handler, parseEventOptions(options))
  }
}

// Remove an event listener from an element
/**
 * @deprecated
 */
export const eventOff = (
  el: Readonly<Element>,
  eventName: string,
  handler: EventListener,
  options: boolean | Readonly<EventListenerOptions> | undefined
) => {
  if (el && el.removeEventListener) {
    el.removeEventListener(eventName, handler, options)
  }
}

// Utility method to add/remove a event listener based on first argument (boolean)
// It passes all other arguments to the `eventOn()` or `eventOff` method
/**
 * @deprecated
 */
export const eventOnOff = (on: boolean, eventParams: Parameters<typeof eventOff>) => {
  const method = on ? eventOn : eventOff
  method(...eventParams)
}
const TABLE_TAG_NAMES = ['TD', 'TH', 'TR']

// Filter CSS selector for click/dblclick/etc. events
// If any of these selectors match the clicked element, we ignore the event
const eventFilter = [
  'a',
  'a *', // Include content inside links
  'button',
  'button *', // Include content inside buttons
  'input:not(.disabled):not([disabled])',
  'select:not(.disabled):not([disabled])',
  'textarea:not(.disabled):not([disabled])',
  '[role="link"]',
  '[role="link"] *',
  '[role="button"]',
  '[role="button"] *',
