          }
        }
      }
    })
  })

  const mobs = !watchChanges
    ? {stop: () => {}}
    : useMutationObserver(
        resolvedContent,
        () => {
          updateList()
        },
        {
          childList: true,
        }
      )
  const scrollIntoView = (event: Readonly<MouseEvent>, smooth: boolean = false) => {
    event.preventDefault()
    const href = (event.target as HTMLElement)?.getAttribute?.('href')
