    }
  }
  const cleanup = () => {
    iobs.stop()
    mobs.stop()
  }
  return {
    current: readonly(current),
    list,
    content: resolvedContent,
    target: resolvedTarget,
    scrollIntoView,
    updateList,
    cleanup,
  }
}
