import {
  type Component,
  computed,
  type ComputedRef,
  type MaybeRefOrGetter,
  type Plugin,
  shallowRef,
  toValue,
} from 'vue'
import {modalControllerPluginKey} from '../utils'
import type {BModalProps, OrchestratedModal} from '../types'
import BModal from '../components/BModal/BModal.vue'

export default {
  install(app) {
    const modals = shallowRef<
      ComputedRef<{
        component: unknown // TS being weird here, just use unknown
        props: OrchestratedModal & {
          _self: symbol
          _modelValue: BModalProps['modelValue']
          _promise: {
            value: Promise<boolean | null>
            resolve: (value: boolean | null) => void
          }
          _isConfirm: boolean
        }
      }>[]
    >([])

    const buildPromise = (): {
      value: Promise<boolean | null>
      resolve: (value: boolean | null) => void
    } => {
      let resolveFunc: (value: boolean | null) => void = () => {
        /* empty */
      }

      const promise = new Promise<boolean | null>((resolve) => {
        resolveFunc = resolve
      })

      return {
        value: promise,
        resolve: resolveFunc,
      }
    }

    const show = (obj: {
      component?: MaybeRefOrGetter<Readonly<Component>>
      props?: MaybeRefOrGetter<Readonly<OrchestratedModal>>
    }) => {
      const _promise = buildPromise()
      const _self = Symbol()

      modals.value = [
        ...modals.value,
        computed(() => ({
          component: toValue(obj.component) ?? BModal,
          props: {...toValue(obj.props), _isConfirm: false, _promise, _self, _modelValue: true},
        })),
      ]

      return _promise.value
    }

    const confirm = (obj: {
      component?: MaybeRefOrGetter<Readonly<Component>>
      props?: MaybeRefOrGetter<Readonly<OrchestratedModal>>
    }) => {
      const _promise = buildPromise()
      const _self = Symbol()

      modals.value = [
        ...modals.value,
        computed(() => ({
          component: toValue(obj.component) ?? BModal,
          props: {...toValue(obj.props), _isConfirm: true, _promise, _self, _modelValue: true},
        })),
      ]

      return _promise.value
    }

    const remove = (self: symbol) => {
      modals.value = modals.value.filter((el) => el.value.props._self !== self)
    }

    app.provide(modalControllerPluginKey, {
      modals,
      remove,
      show,
      confirm,
    })
  },
} satisfies Plugin
import {type ComponentInternalInstance, type Plugin, type Ref, shallowRef, toRef} from 'vue'
import {modalManagerPluginKey} from '../utils'

export default {
  install(app) {
    /**
     * A collection of all currently active modals
     */
    const stack: Ref<ComponentInternalInstance[]> = shallowRef([])

    const countStack = toRef(() => stack.value.length)
    const lastStack = toRef(() => stack.value[stack.value.length - 1])

    const pushStack = (modal: Readonly<ComponentInternalInstance>) => {
      stack.value = [...stack.value, modal]
    }
    const removeStack = (modal: Readonly<ComponentInternalInstance>) => {
      stack.value = stack.value.filter((item) => item.uid !== modal.uid)
    }

    /**
     * A collection of all registered modals
     */
    const registry: Ref<ComponentInternalInstance[]> = shallowRef([])

    // Utility getters not made, would not be used (count, last)

    const pushRegistry = (modal: Readonly<ComponentInternalInstance>) => {
      registry.value = [...registry.value, modal]
    }
    const removeRegistry = (modal: Readonly<ComponentInternalInstance>) => {
      registry.value = registry.value.filter((item) => item.uid !== modal.uid)
    }

    app.provide(modalManagerPluginKey, {
      stack,
      countStack,
      lastStack,
      registry,
      pushStack,
      removeStack,
      pushRegistry,
      removeRegistry,
    })
  },
} satisfies Plugin
import {type Plugin, ref} from 'vue'
import {rtlPluginKey} from '../utils'
import type {BootstrapVueOptions} from '../types'

export default {
  install(app, options: BootstrapVueOptions['plugins']) {
    const rtlDefault = false
    const localeDefault = undefined

    const rtlInitial =
      typeof options?.rtl === 'boolean' ? rtlDefault : options?.rtl?.rtlInitial ?? rtlDefault

    const localeInitial =
      typeof options?.rtl === 'boolean'
        ? localeDefault
        : options?.rtl?.localeInitial ?? localeDefault

    const isRtl = ref(rtlInitial)
    const locale = ref(localeInitial)

    app.provide(rtlPluginKey, {isRtl, locale})
  },
} satisfies Plugin
