  id: undefined,
  label: '',
  labelClass: undefined,
  multiple: false,
  name: undefined,
  noDrop: false,
  noTraverse: false,
  placement: 'start',
  required: false,
  size: undefined,
  state: null,
})

const modelValue = defineModel<File | File[] | null>({
  default: null,
})
const computedId = useId(() => props.id)

